csvContent = `NIF;Empresa;Ventas 2021;Ventas 2022;Ventas 2023;Ventas 2024
A81948077;ENDESA ENERGIA, S.A.U.;78500,00;76200,50;74100,20;72050,00
A95758389;IBERDROLA CLIENTES, S.A.U.;65400,00;66800,00;68900,50;70100,00
B82846817;NATURGY IBERIA, S.A.;45200,00;44100,00;43500,00;42800,00
A33005396;TOTALENERGIES MERCADO ESPA√ëA S.A.;25000,00;26500,00;28000,00;29500,00
B31737422;ACCIONA GREEN ENERGY DEVELOPMENTS SL;6349,69;7441,37;8334,31;7806,73
B26500231;ADELFAS ENERGIA SL;217,54;108,56;232,80;142,54
B25794207;ADEINNOVA ENERGIA SL;81,59;81,47;73,13;98,64
A87623971;ACSOL ENERGIA GLOBAL SA;45,29;36,31;31,83;28,67
B87177408;ABOUTWHITE SL;52,65;31,25;28,05;32,32
B22350466;AB ENERGIA 1903 S.L.;87,37;60,95;50,00;63,79`;

// --- 2. PROCESAMIENTO ---
data = d3.dsvFormat(";").parse(csvContent, (d) =&gt; {
  const row = { "NIF": d.NIF, "Empresa": d.Empresa };
  Object.keys(d).forEach(k =&gt; {
    if (k.includes("Ventas")) {
      let year = k.replace("Ventas", "").trim();
      let val = parseFloat(d[k].replace(",", ".")) || 0;
      row[year] = val;
    }
  });
  return row;
})

// Detectar a√±os
years = Object.keys(data[0]).filter(k =&gt; !isNaN(k)).sort();

// Formato largo para gr√°ficas
data_long = data.flatMap(d =&gt; {
  return years.map(year =&gt; ({
    "Empresa": d.Empresa, "NIF": d.NIF, "A√±o": parseInt(year), "Ventas": d[year] || 0
  }));
})

// Variables Reactivas
mutable currentTab = "comparador"; 
mutable selectedCompany = null;













































&lt;button class="tab-btn ${currentTab === 'mercado' ? 'active' : ''}" onclick=${() =&gt; mutable currentTab = 'mercado'}&gt;
  &lt;i class="fa-solid fa-chart-pie"&gt;&lt;/i&gt; Mercado Global
&lt;/button&gt;
&lt;button class="tab-btn ${currentTab === 'comparador' ? 'active' : ''}" onclick=${() =&gt; mutable currentTab = 'comparador'}&gt;
  &lt;i class="fa-solid fa-table-list"&gt;&lt;/i&gt; Comparador y Datos
&lt;/button&gt;


&lt;div class="fnee-card"&gt;
  &lt;div class="card-title"&gt;Evoluci√≥n del Mercado&lt;/div&gt;
  &lt;div class="card-desc"&gt;Volumen total agregado de las comercializadoras listadas (GWh).&lt;/div&gt;

  ${Plot.plot({
    style: { background: "transparent", color: "#cbd5e1", fontSize: "14px", fontFamily: "sans-serif" },
    height: 400, marginLeft: 60,
    y: { grid: true, label: "Total GWh", tickFormat: "s" },
    x: { label: null, tickFormat: "d" },
    marks: [
      Plot.barY(data_long, Plot.groupX({y: "sum"}, {
        x: "A√±o", y: "Ventas", fill: "#2dd4bf", fillOpacity: 0.8, tip: true,
        title: d =&gt; `${d.A√±o}: ${d.Ventas.toLocaleString("es-ES")} GWh`
      })),
      Plot.ruleY([0], {stroke: "#334155"})
    ]
  })}
&lt;/div&gt;


&lt;div class="fnee-card"&gt;
  &lt;div class="card-title"&gt;Directorio de Empresas&lt;/div&gt;
  &lt;div class="card-desc"&gt;Busca y analiza el hist√≥rico individual.&lt;/div&gt;

  &lt;div style="margin-bottom: 20px;"&gt;
    ${viewof searchTerm = Inputs.search(data, {
      placeholder: "üîç Buscar por NIF o Empresa...", columns: ["Empresa", "NIF"]
    })}
  &lt;/div&gt;

  &lt;div class="table-container"&gt;
    ${htl.html`
    &lt;table class="fnee-table"&gt;
      &lt;thead&gt;
        &lt;tr&gt;
          &lt;th style="width:15%"&gt;NIF&lt;/th&gt;
          &lt;th style="width:45%"&gt;COMERCIALIZADORA&lt;/th&gt;
          &lt;th style="width:25%; text-align:right"&gt;VENTAS 2024 (GWh)&lt;/th&gt;
          &lt;th style="width:15%; text-align:center"&gt;AN√ÅLISIS&lt;/th&gt;
        &lt;/tr&gt;
      &lt;/thead&gt;
      &lt;tbody&gt;
        ${searchTerm.slice(0, 50).map(row =&gt; htl.html`
          &lt;tr&gt;
            &lt;td style="font-family:monospace; color:#94a3b8;"&gt;${row.NIF}&lt;/td&gt;
            &lt;td style="font-weight:600; color:#f1f5f9;"&gt;${row.Empresa}&lt;/td&gt;
            &lt;td style="text-align:right"&gt;
              ${(row["2024"] || 0).toLocaleString("es-ES", {minimumFractionDigits: 2, maximumFractionDigits: 2})}
            &lt;/td&gt;
            &lt;td style="text-align:center"&gt;
              &lt;button class="btn-action" onclick=${() =&gt; mutable selectedCompany = row}&gt;
                &lt;i class="fa-solid fa-eye"&gt;&lt;/i&gt; Ver
              &lt;/button&gt;
            &lt;/td&gt;
          &lt;/tr&gt;
        `)}
      &lt;/tbody&gt;
    &lt;/table&gt;
    ${searchTerm.length === 0 ? htl.html`&lt;div style="padding:40px; text-align:center; color:#64748b;"&gt;No hay resultados&lt;/div&gt;` : ''}
    `}
  &lt;/div&gt;
&lt;/div&gt;


\({(() =&gt; {
  if (selectedCompany) {
    const dialog = htl.html`&lt;dialog class="fnee-modal" open&gt;
      &lt;div class="modal-header"&gt;
        &lt;div&gt;
          &lt;h2 style="margin:0; color:#2dd4bf; font-size:1.3rem;"&gt;\){selectedCompany.Empresa}

      &lt;span style="font-family:monospace; color:#94a3b8;"&gt;${selectedCompany.NIF}&lt;/span&gt;
    &lt;/div&gt;
    &lt;button class="close-btn" onclick=${() =&gt; mutable selectedCompany = null}&gt;&times;&lt;/button&gt;
  &lt;/div&gt;

  &lt;div class="modal-body"&gt;
    &lt;div id="chart-mount"&gt;&lt;/div&gt;
    &lt;div style="margin-top:25px; display:flex; gap:10px; flex-wrap:wrap; justify-content:center;"&gt;
      ${years.map(y =&gt; htl.html`
        &lt;div style="background:rgba(30,41,59,0.5); padding:10px 15px; border-radius:8px; border:1px solid #334155; text-align:center;"&gt;
          &lt;div style="color:#64748b; font-size:0.8rem;"&gt;${y}&lt;/div&gt;
          &lt;div style="color:#fff; font-weight:bold;"&gt;${(selectedCompany[y] || 0).toLocaleString("es-ES", {maximumFractionDigits: 0})}&lt;/div&gt;
        &lt;/div&gt;
      `)}
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/dialog&gt;`;

const chart = Plot.plot({
  style: { background: "transparent", color: "#cbd5e1", fontSize: "14px" },
  height: 350, width: 850, grid: true, marginLeft: 60,
  x: { label: "A√±o", tickFormat: "d", domain: years.map(Number) },
  y: { label: "Ventas (GWh)" },
  marks: [
    Plot.lineY(data_long.filter(d =&gt; d.Empresa === selectedCompany.Empresa), {
      x: "A√±o", y: "Ventas", stroke: "#2dd4bf", strokeWidth: 3, marker: "circle", tip: true
    }),
    Plot.areaY(data_long.filter(d =&gt; d.Empresa === selectedCompany.Empresa), {
      x: "A√±o", y: "Ventas", fill: "#2dd4bf", fillOpacity: 0.2
    })
  ]
});

dialog.querySelector("#chart-mount").append(chart);
dialog.addEventListener("click", e =&gt; {
  const rect = dialog.getBoundingClientRect();
  if (e.clientX &lt; rect.left || e.clientX &gt; rect.right || e.clientY &lt; rect.top || e.clientY &gt; rect.bottom) mutable selectedCompany = null;
});

return dialog;
} return htl.html``; })()}
