raw = await FileAttachment("ventas_boe.csv").text()

// Limpieza de formato (comillas y puntos y coma)
clean_csv = raw.split(/\r?\n/).map(line =&gt; {
  let l = line.trim();
  if (!l) return null; 
  if (l.startsWith('"') && l.endsWith('"')) l = l.slice(1, -1);
  if (l.endsWith(';')) l = l.slice(0, -1);
  return l;
}).filter(l =&gt; l !== null).join("\n")

// Parseo Inteligente
data = d3.dsvFormat(";").parse(clean_csv, (d, i, columns) =&gt; {
  const row = {
    "NIF": d[columns[0]],       
    "Empresa": d[columns[1]]    
  };

  columns.forEach(col =&gt; {
    if (col.includes("Ventas")) {
      let valStr = d[col] ? d[col].toString() : "0";
      // 1.234,56 -&gt; 1234.56
      let val = parseFloat(valStr.replace(/\./g, "").replace(",", ".")) || 0;
      let year = col.replace("Ventas", "").trim();
      row[year] = val; 
    }
  });
  return row;
})

// Filtrar a√±os (BORRANDO 2025)
years = Object.keys(data[0])
  .filter(k =&gt; !isNaN(k) && k.length === 4 && k !== "2025")
  .sort();

// Datos Largos para gr√°ficas
data_long = data.flatMap(d =&gt; {
  return years.map(year =&gt; ({
    "Empresa": d["Empresa"],
    "NIF": d["NIF"],
    "A√±o": parseInt(year),
    "Ventas": d[year] || 0
  }));
})

// VARIABLES DE ESTADO (REACTIVIDAD)
mutable activeTab = "comparador"; // Pesta√±a activa por defecto
mutable selectedCompany = null;




















































&lt;button class="tab-button ${activeTab === 'mercado' ? 'active' : ''}" onclick=${() =&gt; mutable activeTab = 'mercado'}&gt;
  &lt;i class="fa-solid fa-chart-pie"&gt;&lt;/i&gt; Mercado Global
&lt;/button&gt;
&lt;button class="tab-button ${activeTab === 'comparador' ? 'active' : ''}" onclick=${() =&gt; mutable activeTab = 'comparador'}&gt;
  &lt;i class="fa-solid fa-table-list"&gt;&lt;/i&gt; Comparador y An√°lisis
&lt;/button&gt;


&lt;div class="fnee-card"&gt;
  &lt;h3 style="color:#2dd4bf; margin-top:0;"&gt;Evoluci√≥n del Mercado (GWh)&lt;/h3&gt;
  &lt;p style="color:#94a3b8; font-size:0.9rem; margin-bottom:20px;"&gt;Volumen total agregado de todas las comercializadoras del sistema.&lt;/p&gt;

  ${Plot.plot({
    style: { background: "transparent", color: "#cbd5e1", fontSize: "14px" },
    height: 400,
    marginLeft: 60,
    y: { grid: true, label: "Total GWh", tickFormat: "s" },
    x: { label: null, tickFormat: "d" },
    marks: [
      Plot.barY(data_long, Plot.groupX({y: "sum"}, {
        x: "A√±o", y: "Ventas", fill: "#2dd4bf", fillOpacity: 0.8, tip: true,
        title: d =&gt; `${d.A√±o}: ${d.Ventas.toLocaleString("es-ES")} GWh`
      }))
    ]
  })}
&lt;/div&gt;


&lt;div class="fnee-card"&gt;

  &lt;div style="margin-bottom: 20px;"&gt;
    ${viewof searchTerm = Inputs.search(data, {
      placeholder: "üîç Buscar por NIF o Raz√≥n Social...",
      columns: ["Empresa", "NIF"]
    })}
  &lt;/div&gt;

  &lt;div class="table-container"&gt;
    ${htl.html`
    &lt;table class="fnee-table"&gt;
      &lt;thead&gt;
        &lt;tr&gt;
          &lt;th style="width:15%"&gt;NIF&lt;/th&gt;
          &lt;th style="width:45%"&gt;COMERCIALIZADORA&lt;/th&gt;
          &lt;th style="width:25%; text-align:right"&gt;VENTAS 2024 (GWh)&lt;/th&gt;
          &lt;th style="width:15%; text-align:center"&gt;AN√ÅLISIS&lt;/th&gt;
        &lt;/tr&gt;
      &lt;/thead&gt;
      &lt;tbody&gt;
        ${searchTerm.slice(0, 50).map(row =&gt; htl.html`
          &lt;tr&gt;
            &lt;td style="font-family:monospace; color:#94a3b8;"&gt;${row.NIF}&lt;/td&gt;
            &lt;td style="font-weight:600; color:#f1f5f9;"&gt;${row.Empresa}&lt;/td&gt;
            &lt;td style="text-align:right; color:#cbd5e1;"&gt;
              ${(row["2024"] || 0).toLocaleString("es-ES", {minimumFractionDigits: 2, maximumFractionDigits: 2})}
            &lt;/td&gt;
            &lt;td style="text-align:center"&gt;
              &lt;button class="btn-icon" onclick=${() =&gt; mutable selectedCompany = row}&gt;
                üëÅÔ∏è Ver
              &lt;/button&gt;
            &lt;/td&gt;
          &lt;/tr&gt;
        `)}
      &lt;/tbody&gt;
    &lt;/table&gt;
    ${searchTerm.length === 0 ? htl.html`&lt;div style="padding:40px; text-align:center; color:#64748b;"&gt;No se encontraron resultados&lt;/div&gt;` : ''}
    `}
  &lt;/div&gt;

  &lt;div style="margin-top:15px; text-align:right; font-size:0.8rem; color:#64748b;"&gt;
    Mostrando ${Math.min(50, searchTerm.length)} resultados
  &lt;/div&gt;
&lt;/div&gt;


\({(() =&gt; {
  if (selectedCompany) {
    const dialog = htl.html`&lt;dialog class="fnee-modal" open&gt;
      &lt;div class="modal-header"&gt;
        &lt;div&gt;
          &lt;h2 style="margin:0; color:#2dd4bf; font-size:1.3rem;"&gt;\){selectedCompany.Empresa}

      &lt;span style="font-family:monospace; color:#94a3b8;"&gt;${selectedCompany.NIF}&lt;/span&gt;
    &lt;/div&gt;
    &lt;button class="close-btn" onclick=${() =&gt; mutable selectedCompany = null}&gt;&times;&lt;/button&gt;
  &lt;/div&gt;

  &lt;div class="modal-body"&gt;
    &lt;div id="chart-area"&gt;&lt;/div&gt;

    &lt;div style="margin-top:25px; display:grid; grid-template-columns: repeat(auto-fit, minmax(100px, 1fr)); gap:10px;"&gt;
      ${years.map(y =&gt; htl.html`
        &lt;div style="background:rgba(30,41,59,0.5); padding:10px; border-radius:8px; border:1px solid #334155; text-align:center;"&gt;
          &lt;div style="color:#64748b; font-size:0.8rem; margin-bottom:5px;"&gt;${y}&lt;/div&gt;
          &lt;div style="color:#fff; font-weight:bold;"&gt;
            ${(selectedCompany[y] || 0).toLocaleString("es-ES", {maximumFractionDigits: 0})}
          &lt;/div&gt;
        &lt;/div&gt;
      `)}
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/dialog&gt;`;

// Gr√°fico Lineal dentro del modal
const chart = Plot.plot({
  style: { background: "transparent", color: "#cbd5e1", fontSize: "13px" },
  height: 350,
  width: 850,
  grid: true,
  x: { label: "A√±o", tickFormat: "d", domain: years.map(Number) },
  y: { label: "Ventas (GWh)" },
  marks: [
    Plot.lineY(data_long.filter(d =&gt; d.Empresa === selectedCompany.Empresa), {
      x: "A√±o", y: "Ventas", stroke: "#2dd4bf", strokeWidth: 3, marker: "circle", tip: true
    }),
    Plot.areaY(data_long.filter(d =&gt; d.Empresa === selectedCompany.Empresa), {
      x: "A√±o", y: "Ventas", fill: "#2dd4bf", fillOpacity: 0.2
    })
  ]
});

dialog.querySelector("#chart-area").append(chart);

// Cerrar al pulsar fuera
dialog.addEventListener("click", e =&gt; {
  const rect = dialog.getBoundingClientRect();
  if (e.clientX &lt; rect.left || e.clientX &gt; rect.right || e.clientY &lt; rect.top || e.clientY &gt; rect.bottom) {
    mutable selectedCompany = null;
  }
});

return dialog;
} return htl.html``; })()}
