raw = await FileAttachment("ventas_boe.csv").text()

// 2. LIMPIEZA DE ESTRUCTURA (Quitar comillas envolventes)
clean_csv = raw.split(/\r?\n/).map(line =&gt; {
  let l = line.trim();
  if (!l) return null;
  // Si la l√≠nea entera est√° entre comillas, las quitamos
  if (l.startsWith('"') && l.endsWith('"')) l = l.slice(1, -1);
  // Si termina en punto y coma, lo quitamos para que no cree una columna vac√≠a
  if (l.endsWith(';')) l = l.slice(0, -1);
  return l;
}).filter(l =&gt; l !== null).join("\n")

// 3. PARSEO DIN√ÅMICO (SIN ASUMIR NOMBRES DE COLUMNAS)
data = d3.dsvFormat(";").parse(clean_csv, (d, i, columns) =&gt; {
  // columns es la lista de encabezados detectados (ej: NIF, Raz√≥n Social, Ventas 2019...)

  // Mapeo seguro por √çNDICE (para evitar errores de tildes en "Raz√≥n")
  const nif = d[columns[0]]; 
  const nombre = d[columns[1]];

  const row = {
    "NIF": nif,
    "Empresa": nombre
  };

  // Detectar autom√°ticamente columnas de a√±os
  columns.forEach(col =&gt; {
    if (col.includes("Ventas")) {
      // Limpiar numero espa√±ol (1.000,00 -&gt; 1000.00)
      let valStr = d[col] ? d[col].toString() : "0";
      let val = parseFloat(valStr.replace(/\./g, "").replace(",", ".")) || 0;

      // Guardamos el a√±o limpio (ej: "Ventas 2022" -&gt; "2022")
      let year = col.replace("Ventas", "").trim();
      row[`Ventas ${year}`] = val; // Guardamos como "Ventas 2022" para la tabla
      row[year] = val; // Guardamos como "2022" para facilitar l√≥gica
    }
  });

  return row;
})

// 4. DETECTAR A√ëOS DISPONIBLES AUTOM√ÅTICAMENTE
// Miramos la primera fila para ver qu√© a√±os existen realmente en el archivo
years_found = Object.keys(data[0])
  .filter(k =&gt; !isNaN(k) && k.length === 4) // Filtra solo claves que sean a√±os (2019, 2020...)
  .sort();

// 5. DATOS FORMATO LARGO (Para gr√°ficas)
data_long = data.flatMap(d =&gt; {
  return years_found.map(year =&gt; ({
    "Empresa": d["Empresa"],
    "NIF": d["NIF"],
    "A√±o": parseInt(year),
    "Ventas": d[year] || 0
  }));
})
